// Generated by CoffeeScript 1.6.2
$(function() {
  if ($(".qty-row")) {
    $(".qty-row").click(function() {
      //alert("hiiiii1111112222");
      var get, id, bid;
      get = void 0;
      id = void 0;
      bid = void 0;
      id = $(this).attr("data-id");
      bid = $(this).attr("bill-id");
      get = $.get("http://localhost/Supermarket1/HomeController/changeQty/" + id + "/" + bid, (function(data) {
        $("#id").val(data.id);
        $("#bid").val(data.bill_no);
        $("#name").val(data.pro_name);
        $("#pid").val(data.pro_id);
        $("#qty").val(data.quantity);
        $("#productName").text(data.pro_name);
        return $("#edit-qty-modal").modal("show");
      }), "json");
      return get.error(function() {
        $("#edit-qty-modal").modal("hide");
        return alert("Oops couldn't fetch the Client. Please try again." + id);
      });
    });
  }
 
  if ($("#save-qty")) {
    return $("#save-qty").click(function() {
      var post;

      post = void 0;
      post = $.post("http://localhost/Supermarket1/HomeController/saveQty", $("#edit-qty-form").serialize(), (function(data) {
			
			if(data.out == '0'){
				$("#errId").removeClass('d-none');
        		$("#Qlimit").html(data.limit);
        		
        		setTimeout(function(){
        			$('#dit-qty-modal').modal('hide');
   					window.location.reload();
				}, 5000);					
        		
			}else{
				$("#edit-qty-modal").modal("hide");
        		return location.reload();
			}
			
       
      }), "json");
      return post.error(function() {
        return alert("Oops couldn't save. Please try again.");
      });
    });
  }
  
   
  
  
});












